class Item {
    constructor(request) {
        this.request = request
    }

    getItems(params) {
        return this.request.get('IItem/GetItems/v1/', params)
    }

    getItemsById(params) {
        return this.request.get('IItem/GetItemsById/v1/', params)
    }

    withdrawToOpskins(params) {
        return this.request.post('IItem/WithdrawToOpskins/v1/', params)
    }
}

module.exports = Item