class User {
    constructor(request) {
        this.request = request
    }

    getInventory(params) {
        return  this.request.get('IUser/GetInventory/v1', params)
    }

    getProfile(params) {
        return this.request.get('IUser/GetProfile/v1', params)
    }

    updateProfile(params) {
        return this.request.post('IUser/UpdateProfile/v1', params)
    }
}

module.exports = User